(window.webpackJsonp=window.webpackJsonp||[]).push([[112],{477:function(_,v,p){"use strict";p.r(v);var t=p(4),a=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("h3",{attrs:{id:"_1"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_1"}},[_._v("#")]),_._v(" 1")]),_._v(" "),v("p",[_._v("蜀川又飘起了绵绵细雨，酒旗沾了水变得湿重，微风吹拂不动，蒙蒙天光中宛若折翼的大鸟。店小二沽完酒，一边拿着巾帕擦拭酒壶边沥出的残液，一边探头望了望天，嘴里咕哝道：“这都不见青天小半个月了，龙女娘娘心情还没好转么。”")]),_._v(" "),v("p",[_._v("雨声淅沥，无人应答。店小二将那打酒的客人扯进闲聊里：“哎客官，你说说，谁惹得咱们龙女娘娘伤心成这样？这天气往年可不常见啊。”")]),_._v(" "),v("p",[_._v("短檐下站着的白衣青年默了默，道：“兴许是和她夫君吵架了吧。”")]),_._v(" "),v("p",[_._v("“那可真是神仙打架凡人遭罪呐，你看这雨下的，咱们衣褥发霉不说，老百姓生意都不好做。”说着将手里酒壶递给那青年，朝他身后努了努嘴，一头戴笠帽的老伯正挑着扁担匆匆路过。“你瞧那卖木雕的张家老伯，雨天行客少东西卖得也少，他已经抱怨好几天了，本指望着今个儿七夕晚上多卖点给它赚回来，结果，嘿，咱川主大人还没把龙女娘娘给哄好，看来今晚灯会是办不成喽！”")]),_._v(" "),v("p",[_._v("那青年沉默地接过酒壶，闻言倏忽浮动起眼皮，“今日是七夕？”")]),_._v(" "),v("p",[_._v("店小二随口答道：“对啊。说起来客官你成亲了吗？听说这其中门道深得很，我那东家常常说他老婆难……哎等等，你哪里去？喂你别走啊，还没找你钱呢——”")]),_._v(" "),v("p",[_._v("那人头也不回，店小二扯着嗓子喊了好几声他跟没听见似的，背影很快融入在雨里。")]),_._v(" "),v("p",[_._v("“什么人，钱也不要。”店小二低头瞅了瞅手中的铜子，摩挲了两下后悄悄塞到自己腰间。")]),_._v(" "),v("p",[_._v("“听到七夕就跑，怕不是要急着回去哄情人出来耍罢……”")]),_._v(" "),v("p",[_._v("拐进一条巷子里，前后不见人影，杨戬双唇紧抿步履匆匆，抬手甩出去一道金光，东边遂刮起风来，将天上云层呼呼吹散，待他走到家门前，抬头已经可见朗朗青天，金乌的光热终于得以洒到蜀川大地上，万物在无声蒸腾的水汽中逐渐变得清晰且明朗。杨戬从来都不喜欢玉帝和他的儿子，如今站在这日光里心中却也腾然而起一丝希望，长长吐出胸中郁积多日的一口浊气。")]),_._v(" "),v("p",[_._v("府里很安静。哮天犬早先被他赶去华山了，三妹聪慧，从他捎过去的三言两语中窥见这次事情的不同寻常，将哮天犬多留了好几日，嘱咐他务必与寸心解开症结、重修于好。彼时杨戬已喝了一夜的烈酒，捏着杨婵的书信冷笑一声，狠狠砸了手里的酒壶。")]),_._v(" "),v("p",[_._v("去他的重修于好！枉他捧着一颗真心待那龙女如珍似宝，娇花一般的人物，竟眼盲心盲至此，怀疑起那孩子是他杨戬的种来！")]),_._v(" "),v("p",[_._v("他心中又气又痛，决计不愿再看发妻一眼，酒喝多了睡过去时却每每发起魇来，梦里一会儿是从前那人明媚的笑颜一会儿又是山林里沾血的襁褓，画面飞速轮转，渐渐扭曲成一只魍魉的手爪，将他拖进深不见底的漩涡里几欲溺毙，忽而一声细弱的呻吟轻飘飘地落下，迷瘴顿消，眼前景象清晰过来，竟是他发妻涨红着一张脸神情痛苦、纤细脖颈委顿于他狰狞的手掌间，而那孩子正冲着他咯咯发笑......杨戬大骇，急急喘着气从梦中惊醒，满脸水渍分不清是汗还是泪......")]),_._v(" "),v("p",[_._v("他自认于情之一字上有些愚钝，常常猜不透妻子的心思，可即使再愚钝，那一晚的怀疑是真动手是真将一千年的情谊撕碎了撂在两人眼前，纵使他一窍不通也该明白了：夫妻做到他和寸心这样，大概是快要走到头了。这认识让杨戬陡然起了一身的冷汗，从前他与寸心闹得厉害时常常转身就走，呼朋引伴喝酒打猎一去就是不少日子，如今却再不敢走了，终日守在书房或是正门前，听那刺耳的摔打声反而成了慰藉：再怎么闹，总归寸心还是在杨府里闹，他俩还没分开不是么？这么想着，杨戬心里难言的恐惧才能稍稍平下去一些。")]),_._v(" "),v("p",[_._v("庭院短短，不知不觉走到她门前，杨戬忽然停下脚步，深吸了一口气。这样踌躇的画面已上演不知多少次了，他自觉无颜再见寸心，只是灌江口的雨日复一日，再躲避下去他怕是要真的——杨戬闭上眼驱散那想法，想也不愿想。")]),_._v(" "),v("p",[_._v("轻缓的推门声打断出神，案前枯坐的龙女仰头望过来，瞳孔在骤然照进的光里紧缩了一下，只见那人缓步走进来，因逆着光，他的身影有些毛边，看起来不太真切。")]),_._v(" "),v("p",[_._v("她已经很久未见过杨戬了，连日以来的忧思让她先是委屈得红了眼眶，情不自禁撑起身子走向他，转念又想起那晚这狠心人的话，她猛地止住了脚，嘴角轻颤，渐勾起一丝嘲讽的笑意，“没找到吧？”")]),_._v(" "),v("p",[_._v("红唇开合，杨戬没由来的心悸，生怕她再吐出什么刀子般的话，连忙上前一步，“寸心！”")]),_._v(" "),v("p",[_._v("寸心一哽，微微扬起下巴。")]),_._v(" "),v("p",[_._v("杨戬叹了声气，尽力和缓嗓音，“今日是七夕，我们......晚上我们出去走走好么？”")]),_._v(" "),v("p",[_._v("邀约来得太突然，寸心不免愣住。他眼底的期盼明晃晃的，寸心看得明白，却不愿陪他一起装这个糊涂，她和杨戬不一样，凡事她都要求个光明磊落的答案。")]),_._v(" "),v("p",[_._v("“杨戬，你把话说明白。”")]),_._v(" "),v("p",[_._v("说什么？说三妹祝愿的“重修于好”么？那四个字在喉咙里滚过几圈只烫得他心中羞愧，始终吐不出口，他又不愿点破那叫人心痛的现实直言他在粉饰太平，左右为难，倒愈发显得寸心坦率无畏——竟是他的不舍得成全她的“无畏”了。杨戬心中既苦涩且委屈，又不愿面上显出来再与她起争端，转头避开寸心的目光，胡乱寻了个由头，“川蜀已阴雨连绵多时了，百姓生计不好过。”")]),_._v(" "),v("p",[_._v("只见寸心脸色一变，推开他就要往外走。杨戬大惊，登时后悔方才的胡言乱语，连忙拉住她，“做什么？”")]),_._v(" "),v("p",[_._v("“小龙在这儿不是碍到您的百姓生计了吗？小龙这便回西海去，不劳川主大人纡尊降贵特意陪玩。”")]),_._v(" "),v("p",[_._v("她这一声“回西海去”着实踩到了杨戬的痛处，猛地被他拽回去锁在怀里，一身细嫩皮肉教他蛮力箍得生疼，寸心气急，“你发什么疯？我走了不正合你意吗?”")]),_._v(" "),v("p",[_._v("这一声呵斥得他手脚发冷，杨戬眉头一皱，临到嘴边的安抚的话语干涩地失了味道，尽数散去。怀里的龙女曲着手肘横在两人中间，憋得两颊发红，秀眉也拧作一团好似真的厌恶极了他连接触都难以忍受似的，尖锐的话句句扎在他的心上，那铁躯的武神捧着颗血淋淋的凡心沉默良久，忽而悲从中来，“寸心，你是不是从未将杨戬视作丈夫？”")]),_._v(" "),v("p",[_._v("“你究竟把我们一千年多年的感情当作什么？笑话吗？为什么总是不顾我们这么多年的夫妻情谊说出那么混账话来？你是不是……”他攥紧拳头，喉结微动，下了极大决心终于问出口：“你是不是不愿和杨戬再做夫妻了？”")]),_._v(" "),v("p",[_._v("寸心顿住，眼睛一眨，大颗泪珠沿着腮畔滚落，“杨戬，最后一句话你怎么问得出口？你知不知道我这些天、我有多么......”她忽然泄了气，肩头塌下来，一手狠狠攥紧他胸前的衣襟，哽咽得说不出话来。")]),_._v(" "),v("h3",{attrs:{id:"_2"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_2"}},[_._v("#")]),_._v(" 2")]),_._v(" "),v("p",[_._v("今晚的灯会比往年来的热闹得多，街上人潮如织，商贩们卖力吆喝，临时搭起的灯架下灯火熠熠，照耀着烟火人间。七月初七原是女儿家乞巧的佳日，后来人间兴起牛郎织女的传说，这一晚便添上许多旖旎的朦胧，红男绿女结伴出游，杨戬和寸心走在其中也作一对佳人。")]),_._v(" "),v("p",[_._v("走到水边时，卖河灯的摊子多了起来，摊主笑容满面地招揽往来的男男女女，乘着佳节的势头巧立名目，吆喝着：“有情人放河灯，白首不分离诶——”")]),_._v(" "),v("p",[_._v("多年之后再回想起这一晚，寸心总要笑自己和杨戬的糊涂，竟能被那么拙劣的噱头骗了去，几十年前还是中元节用于悼亡的东西，几十年后就被寄以白首之约，而他们两个神仙却和那些凡人一样诚心诚意地对着一个死物发愿，不是糊涂是什么？")]),_._v(" "),v("p",[_._v("然彼时的他们未尝不明白，只是时也，溺水者困于危薮，一根稻草也会被当作救命之物罢了。")]),_._v(" "),v("p",[_._v("花心里灯火明亮，光热扑到她脸上，寸心脸颊隐隐发烫，呼吸也不禁急促了些。一旁的杨戬轻声发问：“写什么呢？”")]),_._v(" "),v("p",[_._v("“写，长相守……好不好？”她的眼睫微微颤抖，低垂的目光缠绵上身边人的衣角，在那一刻十分虔诚地发愿。")]),_._v(" "),v("p",[_._v("她心中还是有千般不甘和不舍，对郎君恨极也爱极反倒愈发茫茫然不知所措，白日里教他拿戳心窝子的话一捅，连日以来的心绪突然决堤，被他怀里的温暖熏红了眼眶终于不管不顾地大哭了一场，满心只剩下一个念头：她实在不想与杨戬分开。")]),_._v(" "),v("p",[_._v("“好。”杨戬颔首，取笔蘸饱了墨，屏住呼吸，一笔一划郑重写下：长相守。")]),_._v(" "),v("p",[_._v("付讫银钱，寸心小心翼翼捧起河灯，转身走向对面河边。长夜里河水透黑，粼粼映着岸上玉壶光转灯火璀璨，水面上河灯四散，近看似阆苑仙葩远望若明珠荡于其间，冶丽间果若真有几分神性之美，寸心看得入了迷，不由得起了“兴许是真的灵呢”的念头，顿觉心头恍若生了只灵动翩飞的雀儿周身也轻快起来，仰头与杨戬相视一笑，他眼中灯影跃跃，其下也流淌着喜悦和希冀。")]),_._v(" "),v("p",[_._v("电石火光间心境有刹那阒然又动若擂鼓，一眼万年。")]),_._v(" "),v("p",[_._v("“嘭——嘭——”两声，漆黑夜幕里突然绽开烟火，河对岸不知何时搭起了烟火架子，转眼火树银花琼彩满天，河边视野宽阔，人潮涌过来，寸心正仰头观望，一个短腿娃娃突然欢笑着从她与杨戬双腿之间钻过去，紧接着是那孩子的母亲追过来，寸心被她动作急切地一推踉跄着旁退好几步，人潮忽然如入海奔流辟开沙洲一般将他二人冲散，再转头只能从纷乱行影中仓惶寻找杨戬的身影。")]),_._v(" "),v("p",[_._v("“寸心——”")]),_._v(" "),v("p",[_._v("烟火喧闹人声鼎沸，她方欲张口回应，前倾的身子蓦地被人一撞怀里的河灯也失了控向前飞出去，火苗滑过双手她只捞了把虚妄的余热，极轻极轻地“咔嚓”一声，漂亮的花瓣碎在行人脚下，泥水溅上去，火光挣扎两下，黯然熄灭。")]),_._v(" "),v("p",[_._v("“寸心——”那声音近了些，更添了难言的悲绝。")]),_._v(" "),v("p",[_._v("人潮如织里，龙女忽然泪流满面……")]),_._v(" "),v("h3",{attrs:{id:"_3"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_3"}},[_._v("#")]),_._v(" 3")]),_._v(" "),v("p",[_._v("门被大力合上，紧接着又是“哐当”一声，两道人影迫切地抵在门后，杨戬一手抓着她的腰一手揉捏按着她的后颈，寸心仰头迎合，急促气息相互纠缠湿漉漉的吻凶狠又缠绵，舌尖勾在一起不着章法又吮又舔呼吸也乱得不成样子，直到齿关叩上嘴唇，一股腥咸化开，这场闹剧才匆匆止歇。")]),_._v(" "),v("p",[_._v("两人交颈相拥，寂静室内一时只闻得凌乱地喘息。杨戬偏头含住她的耳珠，呼吸颤巍巍喷到她的耳后，“都是假的，一个河灯罢了，别怕……”")]),_._v(" "),v("p",[_._v("“嗯……”寸心含糊应了一声，泪痕未干，只觉得初秋的夜晚未免太过寒凉，浑身都发冷，她动作迫切地揪住丈夫的衣襟，“杨戬，杨戬，今晚你不许走。”")]),_._v(" "),v("p",[_._v("错落的吻落到她白嫩肩头，“嗯，我不走。”")]),_._v(" "),v("p",[_._v("鬓发松散簪不住钗环，腰带挑落衣衫半褪堆在臂弯里，小衣半遮，抓取后呼出一对饱满的玉乳，灯下美人肤如凝脂漫上一层潮湿的绯色，寸心被杨戬抱到窗前妆台上，胭脂水粉一阵“哐啷”落地洒得二人的罗裙长袍纨裤上星星点点，她起身想去看，杨戬忽然俯下身到她胸前作弄，二人分房多时寸心分外敏感，这一下教她彻底软下来，玉趾难耐地蜷缩起来，扶着他的肩进退两难。")]),_._v(" "),v("p",[_._v("杨戬只觉口中香甜手里滑腻饱满，寸心的喘息成了最烈的催情物，胯间涨得生疼，手从胸前揉捏到她柔韧的腰肢又绕到尾椎骨最后滑到她腿心，掌心和手指粗糙的陈茧摩擦着激起她一阵颤栗，春水汩汩龙女馨香也更加馥郁，随着他的节奏又泣又喘，双腿也不住地蹭着绞着他的腰。")]),_._v(" "),v("p",[_._v("“给我，杨戬。”巨大的空虚与悲哀裹挟着她，这水里来的龙女泪盈于睫呵兰吐气，乳首红樱沾满他的涎液在灯烛下晶亮地俏立着。")]),_._v(" "),v("p",[_._v("杨戬抄起她匀亭双腿挺身进入，湿热的层层叠叠的花瓣紧紧吸裹住他，惶惶多日在这一刻终于得到了羁鸟归林般的熨帖，杨戬绷紧劲腰缓缓沉进去，贪恋地凝视着妻子因他而起的每一个反应。寸心抓着他肌肉贲发的臂膀，额头沁出细密的汗珠，娇软身躯在他的顶撞下愈发滚烫红润，初时的不安和绝望早在一次次头皮发麻中被抛却脑后，任由自己溺入欲海中去寻解脱。")]),_._v(" "),v("p",[_._v("又胀又硬的物什杵着娇嫩的花心温柔厮磨，寸心倚在杨戬肩头长长喘息着，好似徜徉在傍晚时分近岸的海水里，暖洋洋的将她整个人包裹住，她收拢手指，想要留住这一刻的缱绻温存。忽而攻势转猛，杨戬坏心眼地剧烈动作起来，娇嫩花径就有些受不住疾风骤雨了，快感铺天盖地羞人的娇吟难以自抑，寸心一手勉力勾住他的脖颈，身子后仰纤腰轻颤，断断续续地央他轻一些。")]),_._v(" "),v("p",[_._v("激烈间窗子被撞开，杨戬一手捧着她的后脑整个人伏在她身上，夜风微凉，吹得她灵台略清明了些，寸心睁开眼睛，视线模糊又摇晃，朦胧见到旷远星野里一条银带。")]),_._v(" "),v("p",[_._v("是凡间传说里王母娘娘划的天河，拆散了织女和牛郎……她勉力思索着，泪水不知何时簌簌滚落。杨戬的手抚上来，“我弄疼你了？”寸心用力摇了摇头，双臂紧紧环住他的脖颈，闷声道：“再用力一些。”")]),_._v(" "),v("p",[_._v("情事酣畅，从窗前辗转到榻上，二人纵情胡天胡地地闹，情潮迭起恨不得融入彼此。不知第多少次后，寸心累得伏在软枕上歇息，半截藕臂上红痕点点，凌乱碎发粘在通红汗湿的脸上，杨戬从后贴上来，轻轻拨开湿黑的鬓发，在她眼角落下一吻，灼热鼻息渐渐下移喷到后颈，薄唇在那里反复逡巡却始终落不下去。寸心余韵未歇不愿理他，不耐烦地一脚踢过去，软绵绵的没什么力道，被杨戬捉在手里轻轻摩挲她的踝骨。")]),_._v(" "),v("p",[_._v("“别——”寸心刚挤出一个音，突然感受到颈窝里的潮意，滚烫的泪水滑过她的肌肤陷入床褥里，留下深色的水痕。杨戬捏着她玲珑脚腕轻轻一扯，倾身覆上去，寸心骤然绷紧，喉咙里溢出一声哼叫，俄而腰臀柔软下来，将自己全数交出去......")]),_._v(" "),v("p",[_._v("帷幔里艳色无边，此夜不思离恨。")])])}),[],!1,null,null,null);v.default=a.exports}}]);