(window.webpackJsonp=window.webpackJsonp||[]).push([[169],{540:function(v,_,p){"use strict";p.r(_);var t=p(5),n=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("p",[_("RouterLink",{attrs:{to:"/pages/17c160/#【番外·一斛珠】"}},[v._v("一斛珠")]),v._v("·续")],1),v._v(" "),_("hr"),v._v(" "),_("p",[v._v("夫妻二人于云上走过半途，忽然龙女一拽杨戬衣袖，生生将他逼停，呼道：“了不得，险些忘记大事！”")]),v._v(" "),_("p",[v._v("杨戬本是心口堵着一口气，正急忙赶回家欲教训教训这胆大包天的老婆，奈何教她一止，梗着的一股愤愤也一时戳破了泻出五分，只好顿下首，无奈地看着她：“怎么？咋咋呼呼地。”")]),v._v(" "),_("p",[v._v("“我不是与你说了来意……”眼见天色擦黑，寸心愈发着急，“我顶了婉婉的身份助她脱困，目下我倒溜了，琅琳馆找不见人，她怎得跑远？”")]),v._v(" "),_("p",[v._v("杨戬只觉额头的青筋在跳，忍不住按了按太阳穴，已经有些着恼：“那怎么办？你一辈子抵那馆子里扮她？”")]),v._v(" "),_("p",[v._v("寸心见他面色不好，自己又理亏，不由有些怯懦，小心翼翼地抱住丈夫的手，轻轻摇了摇：“当然不是……我放着好好儿地川主夫人不做，稀得伶人之身嘛？就是，就是，帮人帮到底……二郎，你就允我这一回……你最好了嘛……”")]),v._v(" "),_("p",[v._v("郎君轻飘飘地看她一眼，心中默默地叹了口气，也是受用极了她撒痴卖娇，纵使万般地不情愿，也仍旧松口：“行了，那你说，怎么办？”")]),v._v(" "),_("p",[v._v("寸心觑着他的脸色，小手一下一下轻轻抚着他胸口的衣料，糯声说道：“今夜……本是婉婉的招婿之夜……”她见杨戬的脸色立刻便要不好，连忙更凑近了些，蹭了一蹭他僵直着的臂膀，声音愈发娇软：“二爷一会儿去领个花牌，把我买了……在这楼里歇上一夜，明日我们便走……如此婉婉便可顺理成章地脱身，你看好嘛？”")]),v._v(" "),_("p",[v._v("她自然晓得自己夫君刚直，这等荒唐之事想来也是看不过眼，更罔论陪着她一齐胡闹的，因此更是用了十二万分的讨好相待。只这春衫单薄，两团柔软在小臂处磨蹭已久，早勾着他欲动半晌，此刻听她说这“招婿”，不由“啧”了一声，将手搭在她圆润的肩头，往怀里一带，沉声说道：“是不是那鲛女把你带坏了，你哪里去学来的？”")]),v._v(" "),_("p",[v._v("这姿势颇为暧昧，又隐隐带着三分调戏的味道，寸心虽不懂他们人类中登徒子自爱做哪般行径，但此处是青天白日的云间，一时粉腮嫣红，象征性地推了推，嗔道：“你答不答应嘛？好二爷，求求你了，只一回，成不成？你就帮帮我嘛……我一个人不行的……”")]),v._v(" "),_("p",[v._v("杨戬被她这几声软语说得心尖儿都甜软起来，颇为无奈地应是：“好好好，夫人的事便是我的事。只是记着了，以后遇见这等事，要先与我商量。”见她点头如捣蒜般殷勤答应，又微微一笑，二人携手复又向琅琳馆而去。")]),v._v(" "),_("p",[v._v("到了馆外，杨戬先魂魄出窍去查探，果见那分管琅琳阁前众伶女的老鸨急得发疯，呼喝护院到处寻人，四下匆匆忙忙地，几乎连馆内的宴事都停了。幸得此刻时辰尚早，这妓馆真正的营生还未开张，来往之人并不多。于是二人先潜入馆中，寻了一个偏僻无人的院落，向杨戬衣衫洒上许多酒液。寸心变回婉婉的模样，搀着他甫一出院门，便被两个急匆匆的婆子逮住，连声道：“我的好姑娘，跑到哪里去了！可将老妈妈急坏了！”")]),v._v(" "),_("p",[v._v("寸心做嘘声状，故作为难，低声道：“这位公子今日点了我去，纠缠至如今，他位高权重，我怎敢得罪？”言语间神情楚楚可怜，一身湖蓝色的襦裙衬得人愈发娇小，兼之勉力搀着身形高大的郎君，一时将两个疾言厉色的婆子也软下心来，切切道：“那也罢了，今夜可是姑娘招婿之夜，老妈妈将你看得像个明珠似的，身子可没被占了罢？”")]),v._v(" "),_("p",[v._v("绕是龙女已为人妇，听此粗俗之语也是面红耳赤，扭捏着答道：“没有，没有……公子说了，今夜自买了我家去。你们快去前院寻那个黑衣护院，让他为公子领一道花牌。我搀他去歇息，安顿好了就回楼中去。”")]),v._v(" "),_("p",[v._v("两个婆子狐疑地看了她两眼，却见这男子蜂腰猿背，华服玉带，即使低着头，只瞧得见半张脸，也能看得出他俊美非常，难怪小妮子急着要跟这上佳的主顾拉拉扯扯。既如此，她二人自然赶去为他领花牌去了，这有了私情的男客，喊起价来可更是起劲儿呢。")]),v._v(" "),_("p",[v._v("——")]),v._v(" "),_("p",[v._v("果如她二人所想，当晚这招婿招到泰半，这一身阔气的风流贵公子果然点了天灯，一举万金拍得这位名动苏杭的婉婉姑娘，直使一众嫖客自叹弗如。老鸨欢欢喜喜地张罗着，邀在坐各位合饮一杯喜酒，众嫖客都是风月场上摸爬滚打惯了的，见此更不着恼，反而欢欢喜喜地庆祝起来：“易公子真是财大气粗，咱们不可辜负这万金的喜酒才好啊！”")]),v._v(" "),_("p",[v._v("只二楼中等着接应的邵阳将军心中默默然，暗暗想道：“原说这二郎真君正经，想不到也是个风流倜傥之人。只可惜了他家中的贤妻，背家弃孝却嫁得这样男人。”")]),v._v(" "),_("p",[v._v("那厢嫖客却极是善解人意，这花楼里头招婿，都是给姑娘按出嫁办的，一伙儿人打哨子起哄，都说甚么洞房花烛夜，春宵一刻值千金，俱推“易公子”进了洞房，然后自顾找自己的姑娘玩儿去了。")]),v._v(" "),_("p",[v._v("这厢杨戬跨入红澄澄的喜房，嫌恶地脱下外衫扔在一旁，三两步向软床走去，声音带着丝委屈：“可遂愿了？直教人看了我出这‘大风头’，你的好姐妹可真抵得上我的名声。”")]),v._v(" "),_("p",[v._v("寸心在帐中喷地一笑，怎听不出他的醋意？早在侍女出门后便已幻回了本相，在妆台前更细细描摹了本就浓艳姝丽的妆容，坐在帐中静待杨戬，此刻听见他的脚步声，遂伸出一双白玉似的藕臂拨开红纱，软声唤他：“好二爷，辛苦你了，寸心在此给你赔不是了嘛。”")]),v._v(" "),_("p",[v._v("杨戬闻声，抬眼望去，只见重重的红雾纱帐之后印出一影影幢幢的女体，周身似佩玉戴珠，于烛火葳蕤间闪出晶晶亮光，更衬得这曼妙的酮体如珠似玉，媚色撩人。他虽见多识广，却晓得非礼勿视，从不曾见此香艳之景，一时面色膨地涨红了，周身血流如注，脑海中浮现出那勾了自己一整日的白软柔颈，不由得脚步沉沉，缓缓走到那红帐前，低声道：“怎么赔不是？说一句辛苦就算了了么？”")]),v._v(" "),_("p",[v._v("那鸾帐以金钩挂住半帘，寸心双手拨开，探出半边身子，只见她发顶红绸——这琅琳馆的盖头也不正经，哪有还未掀开就映得红纱之下的美人肤如凝脂，粉面桃腮的？杨戬低下头，伸手捧住了龙女隔着红绸的脸，只见她晶亮的眼眸映照着烛火，唇上的胭脂粉嫩可人，满头的发饰琳琳，在灯火下反射出耀眼的光，更衬得她明媚鲜艳，仿佛一株盛开的海棠花。")]),v._v(" "),_("p",[v._v("杨戬有些陶醉，缓缓地低下身，隔着薄如无物的红纱绸锦，亲吻她粉嫩的唇。")]),v._v(" "),_("p",[v._v("二人仅聊解爱意，不过一触即分，寸心笑吟吟地看着他，伸出藕臂揽上他的项子，软语道：“赔你一场洞房花烛夜，你看值不值得？”")]),v._v(" "),_("p",[v._v("杨戬心中一动，想到许多年前他出征西岐的那个夜晚，月影婆娑、水光粼粼，洞房花烛之中，二人大吵了一架，放着红逍帐暖不入，偏在那冷硬的凉亭中凑合了一宿。")]),v._v(" "),_("p",[v._v("他不由心中稍软，轻声叹道：“并不怪你，是我不好，该我赔你的。我是你丈夫，你的事都该是我的事。寸心，我以后……”他未能说出以后如何如何，便被她嚅满了笑意的眸子盯得没了声音，一时失笑，“你笑什么？以后我该加倍对你好，成不成？可你也不许再气我。”")]),v._v(" "),_("p",[v._v("来龙女半身挂在他项子上，一面笑着，一面连连点头，说道：“好嘛，好嘛。只要二爷不再生我的气，就是以后没有加倍的好啊，也没什么关系的。”")]),v._v(" "),_("p",[v._v("“行啊，那我现下不生气了，以后也并不对你好了。”")]),v._v(" "),_("p",[v._v("“喂！臭杨戬！”寸心嘟起嘴，“你的好话能不能超过三句啊！”")]),v._v(" "),_("p",[v._v("那绯红的盖头随着二人打闹斗嘴，径自飘落，杨戬将软帐挂上金钩，为她卸下一头玲琅满目的首饰，坐在她身后替她以木梳梳理那一头海藻般柔顺的乌发。")]),v._v(" "),_("p",[v._v("他有些迷醉地看着掌心的墨发如丝绸一般自指尖滑落，侧头一嗅便能闻到她发上的幽香。和素日里她身上天润又带些奶味的气息不同，恐是阁前经了打理，听人说这般地方的女子招婿前原要以香药泡澡，熏蒸特制之药的。")]),v._v(" "),_("p",[v._v("正这样想着他感到身上也有些发热，不由得自身后拥住她，埋入她白嫩的颈脖处深深吸了口气，叹道：“味道有些不同。”说着，便要伸手去拨开她的外袍。")]),v._v(" "),_("p",[v._v("她被这大红的袍子裹在其中，锁骨处都包得严严实实，在这并不清爽的夏夜显得有些另类，何况龙女本是最最惧热的。他心头已经有异，不想寸心反手一格，面色通红，嗔道：“你干嘛！这是在外头呢！”")]),v._v(" "),_("p",[v._v("“？”杨戬喷地一笑，“不是你说要赔我的么？”")]),v._v(" "),_("p",[v._v("“在这种地方……”寸心咬了咬嘴唇，羞答答地缩在杨戬怀中扭捏，“咱们洞房花烛不是……只意思一下……走个形式也就……杨，杨戬，你真的要在这里……”")]),v._v(" "),_("p",[v._v("他将她暧昧地圈住，呼吸灼烫了，叫她纤白的雪颈瑟瑟发抖，不一时杨戬一口含住了她的耳垂，声音低哑而模糊，“洞房花烛怎能不行周公之礼？好姑娘，你今晚可是被本公子万金买来的，说什么推拒之词，还不快快伺候？”")]),v._v(" "),_("p",[v._v("杨戬只觉怀中娇小的身躯蓦地一颤，声音又软又柔：“要，要怎么伺候？”")]),v._v(" "),_("p",[v._v("“啧，”郎君兴味大起，忠实地与她扮演起这“嫖客”与“清倌”的角色，“纵使你只是个伶人，既然要招婿，总会教你罢？小娘子，别装了，快来伺候你的新夫罢。”")]),v._v(" "),_("p",[v._v("寸心面上一红，忽然想起婉婉于闺房中她无意翻看到的那本小册子。且当时为类伶人身姿，这鲛女与她说了不少个中秘事，又见她不小心看了眼书册，竟面红耳赤，不由奇道：“我早闻公主殿下已经嫁人，怎么竟不晓房中事么？何以羞成这样？”")]),v._v(" "),_("p",[v._v("龙女年岁尚浅，家中如珠似宝，在家中有父母宠爱，养得她极为单纯，虽则嫁人，她与杨戬少年夫妻，虽时有争吵，但夫君也多有呵护，她房事羞涩，郎君即温柔以待，断不敢以这百般手段相欺。是以当婉婉同她说些甚么“最好用嘴便教他走了”、“如此也能少受些苦”之类的话，一时羞涩得不敢再听，一时又因着实在好奇将话听得全乎，总之该学的、不该学的，都听了个十之八九。")]),v._v(" "),_("p",[v._v("此时杨戬作此情状，她不禁想到白日婉婉所说的，如何“伺候”恩客之语，而此刻既他是自己的“恩客”，难道，难道他是要她……")]),v._v(" "),_("p",[v._v("寸心蓦地脸红，小爪子一把推开他，嗔道：“你，你虽然这回是占理的，可，可也不要太过分了！”")]),v._v(" "),_("p",[v._v("其实杨戬只不过信口胡诌几句，断断没有那样的意思，见她如此怕羞，不由喷地一笑，仰倒在软绵绵的床上，嘴中仍不忘逗她：“是谁过分了？我花了那么多钱，你不该伺候我睡觉？什么道理。”")]),v._v(" "),_("p",[v._v("龙女心头一跳，暗道：“他果然想的是那睡觉的事！”奈何此次实为求人，一时心中有些隐隐生气，只想着这人怎么那样过分，纵使是她麻烦了他这一回，可既然是夫妻，就惯着她一次会怎样呢？一时又想，既然你要这报酬，我自给你好了！给完两不相欠，再也不要理你这混蛾子！")]),v._v(" "),_("p",[v._v("一时想定，寸心猛地将身上裹着的外衫掀开，分腿骑上他腰，说道：“睡觉就睡觉！你就会欺负我！等过了今晚，我再也不理你了！”")]),v._v(" "),_("p",[v._v("杨戬先是一惊，举目望去，竟见她身上衣料单薄，隐隐透着美肤玉乳，又在那不该露骨之处薄透，纤腰处盈盈一握，一根极细的束带将纱衣系拢，更衬得她酮体莹润，凹凸有致，玉体横陈。一时间心中扑通扑通地乱跳，鼻尖一麻，心中只道：原是她穿着这衣衫，才不给我看的。")]),v._v(" "),_("p",[v._v("寸心见他怔怔地盯着她的身子，眼睛都看直了，只在心中骂了一句：色鬼！嘟着嘴巴捶了他一拳，眼眶微红，说道：“看什么！你可满意了？！”")]),v._v(" "),_("p",[v._v("杨戬这才从那令人移不开眼的娇躯上回神，生受了这一拳，愣愣地说：“什么？”")]),v._v(" "),_("p",[v._v("“你这会儿又装什么了，”寸心很是委屈，她是说风就是雨的性格，惯会卖痴耍娇，倘使不高兴了，眼泪也是说来就来，嘴角一撇，眼中已是泪光涟涟，“我为你做就是了，既然受了你的恩，受你欺负也是应该的！”说着便双手摸索到杨戬下腹，欲解开他的衣带，回身拨开四散的秀发，竟将脸缓缓帖向他下处。\n　\n真君正是个血气方刚的男儿，方才见心上人那般诱人之姿，魂儿都勾去了大半，怎压得住枪？早已直挺挺地抵在她软臀处，此时她爬到郎君双腿间，竟似欲以口弄之，杨戬一惊，连忙起身制止她的动作，“寸心，你……”话未出口，竟一时顿住，只见她神情端地楚楚可怜，仰头望着他时，眼角尚有泪珠。")]),v._v(" "),_("p",[v._v("在杨戬心中，寸心向来是最为高贵矜傲的公主，穿的要顶好，用的要顶好，吃的也要顶好，可此时这位应被千万人捧在掌心的矜贵龙女，竟趴在自己胯间侍弄这粗俗的浊物，一时间，他身为男子的征服欲得到前所未有的满足，竟停下了阻止她的手，任由她解开腰带，褪下亵裤，放出那紫红色涨怒的巨硕，啪地一声打在她微张的樱唇上。")]),v._v(" "),_("p",[v._v("杨戬的身子蓦然一紧，只觉她的唇又弹又软，滑嫩异常，一时间血脉贲张，面色彤地一红，紧紧抓住身下的锦褥，缓缓靠在了身后那铺着软枕的梨花木床头，微微咬紧牙关。")]),v._v(" "),_("p",[v._v("视觉的刺激消逝后，感官竟愈发明显，他只觉龙女两只温软的柔荑抚上这阳根，接着顶端传来一阵柔软和潮湿，一根又滑又腻的香舌在舔牴抚慰着他怒涨的阳具，一时间只如登上极乐之巅，心理与生理的双重满足，令他忍不住重重地叹息一声。")]),v._v(" "),_("p",[v._v("正是浑身酥麻，飘飘欲仙之时，忽然耳闻一声细微的啜泣，杨戬一惊，连忙起身去看她，却见龙女眼尾泛红，边缓缓地舔着他，边悄声啜泣。")]),v._v(" "),_("p",[v._v("郎君眉头一皱，抬手勾起她的下颚，叹道：“既然不愿意，为什么还要做呢？”")]),v._v(" "),_("p",[v._v("“……你，你，”龙女倔强地看着他，断断续续的抽咽却叫她半分严肃不起来，反而愈显可怜可爱，“你不是很舒服嘛？婉婉和我说，还有，还有书上也是这么写……”其实她也羞得要命，往日杨戬与她行事，俱是夜黑风高，且又怜惜她初经人事，从不曾以此物相辱，方才她一扒开他的亵裤，竟被这浑物打了个措手不及，待近看这物，更是心惊，又有一种奇异之感，只暗暗想道，杨戬这人身材英武，气宇轩昂，一张脸更是龙章凤姿，三界无处其右者，怎么胯下的这东西却如此丑陋，长得真吓人，还夜夜都欺负于她，实在太坏了。")]),v._v(" "),_("p",[v._v("其实她见少识窄，万不曾晓得，杨戬此处于世间须眉浊物中已是翘楚，不说粗长硕大，顶得龙女一截藕臂形容，更难得姿优形美，平日并不涨起时粉嫩干净，而与她亲近时则充血涨为紫红色，柱身布满了浴火难炽的青筋，是以观止狰狞，实在属个中名器。")]),v._v(" "),_("p",[v._v("按着性子舔了两下，寸心愈想愈伤心，恼他竟将自己真当粉头对待，不由委屈到了极点，一面做着，一面呜呜地哭起来。")]),v._v(" "),_("p",[v._v("杨戬真是哭笑不得，忙要抱她起来，可这小龙硬梗着性子不给他碰，一时无奈，说道：“好了，是我不对，成不成？那鲛女将你教坏了，怎么就肯听她胡说些不三不四的，快起来。”")]),v._v(" "),_("p",[v._v("寸心一面哭，一面与他僵持：“我不要！你们男人不都喜欢如此么？难道你嫌我做得没有别人好，你不舒服？”")]),v._v(" "),_("p",[v._v("“……这却怎么比了！”杨戬面色铁青，“我又没和别人……我怎么知道！”")]),v._v(" "),_("p",[v._v("倔强的小哭包瞪着眼睛还要反抗：“那你说你舒服了，我就起来！”")]),v._v(" "),_("p",[v._v("杨戬只觉得脑袋气得突突跳，愤愤道：“我不说！哼，一点都不舒服！”")]),v._v(" "),_("p",[v._v("这厮和她对着干简直是意料之中的事，寸心咬咬牙，哼了一声，扶着他低头一舔，堪堪含住半个龟头，努力地嘬弄起来。")]),v._v(" "),_("p",[v._v("香软的小舌温柔地包裹住他，龙女仿佛无师自通，做得越来越顺口，将他的棒身口得水光淋漓。杨戬呼吸已经逐渐粗重，面色潮红，强忍住向上顶弄的冲动，直勾勾看着面前衣衫单薄，格外惑人的妻子。她卖力地服侍着他，就连衣缕渐乱，胸前两团白嫩的软乳左右摇摆、春光大泄也不曾留意，真如一颗香甜奶糖，令人忍不住想拆吃入腹。")]),v._v(" "),_("p",[v._v("他忍不住自喉间溢出几声喘息，双手扶上龙女细嫩的肩膀，强忍住按她脑袋的冲动，微向上顶，脑海里一片空白。寸心见状，心中欣喜，暗暗想道：“我做的原是不错。”愈发卖力，只听这红帐之中喘息声愈发重，间或有啧啧吸舔物品的水声，不多时，杨戬闷闷地哼了一声，猛地将她抱起，将脸埋入龙女肩窝之中射了她满手。")]),v._v(" "),_("p",[v._v("龙女的脸蓦地通红，举着满手白浊，更不好打他，一时啐道：“你这不知羞的，不是说你不舒服？怎么又……又……”")]),v._v(" "),_("p",[v._v("此刻杨戬还回味着这龙女娘娘屈尊降贵的头一遭，其实若论起，断无她这软嫩可欺的绝世艳体爽快，只是从未玩过此等花活儿，心理上的满足自然是无可比拟的。他依恋地靠在她怀中，轻声哄道：“好夫人，我知错了，再不敢说。只不过这等事，我从未想你做的，寸心，你明白我的心意嘛？”说着他看向龙女，眸光亮晶晶地，在这昏暗的床帐内显得格外动人。")]),v._v(" "),_("p",[v._v("“我……”寸心咬咬唇，“你不说，我又哪里明白去？”")]),v._v(" "),_("p",[v._v("杨戬轻轻笑着，拿过一张丝帕，为她仔仔细细地擦拭着双手，柔声道：“你只要做公主就好了，什么都不用想，什么都不必操心。我知道你来此为何，可你知不知道我今日所会邵阳将军，便是为了接应鲛人族少主而来？”")]),v._v(" "),_("p",[v._v("寸心一愣，说道：“……婉婉？”")]),v._v(" "),_("p",[v._v("“他托我引荐你兄长，已是为这平乱鲛人族之祸，我思月下云梦湖龙儿新诞宴席，自然带你及早相赴，书信与摩昂太子。谁知道你和那鲛人少主竟有私交，又不告诉我。”他说道此处，似乎有些不被信任的委屈，轻轻哼了一下，三两下替她擦完手，将那丝帕扔出床外。")]),v._v(" "),_("p",[v._v("龙女心中一转，已知端的，见他面色不渝，心中也泛起一丝愧疚，连忙勾住他的项子，在他颊上香了一口，软声道：“好嘛，我知错了，不该错怪你到这里，也不该多事来此。”她顿了一顿，又爬起来亲他一口，窥着他脸色问道：“不过你和我说实话，真的没来过？”")]),v._v(" "),_("p",[v._v("杨戬眉头一皱，无奈地看着她：“真的没有！”见她小口微张，似要再问，心中警铃大作，猛地将她翻身压住，吻如雨点般落下，将她亲得支支吾吾，一个劲地讨饶，“不是，唔……我信了，信了唔还不成……”")]),v._v(" "),_("p",[v._v("郎君的手熟稔地钻到她裙子下面，满脸热切：“既然话说开了，咱们就接着办事……好夫人，离家这么多天，可想死我了……”这馆子的衣裙专是为这风月之事所制，格外地好脱，只几个来回，他便将身下的女郎剥了个干干净净，眼前盈盈摇曳着勾了他一夜的嫩乳，将脸一埋，张口就啃。")]),v._v(" "),_("p",[v._v("寸心嘤嘤地求饶道：“你是狗吗？你轻一点啊——方才明明已经……”")]),v._v(" "),_("p",[v._v("杨戬囫囵地呜呜道：“方才怎算？你莫要被人给骗了，这等事，只有我教你的才作数……”话未说完，他已熟稔地寻到那湿滑小口，猛地一挺身，将自己送了进去。")]),v._v(" "),_("p",[v._v("寸心难耐地一哼，只得紧紧缠住他的肩膀，感受到男子热烈的汗珠低落到自己身上，嗯嗯啊啊地呻吟，连声娇呼：“好哥哥，轻一点！”")]),v._v(" "),_("p",[v._v("杨戬只在她身上起起伏伏，夜半子时，屋中依然灯火通明，淫声阵阵，当日一手劈落九日的勇猛郎君，瘫在这娇软女郎的肚皮上，半分不愿相离。")])])}),[],!1,null,null,null);_.default=n.exports}}]);