(window.webpackJsonp=window.webpackJsonp||[]).push([[177],{548:function(v,_,p){"use strict";p.r(_);var t=p(5),n=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("p",[v._v("自弱水下界，为拯救人间生灵，众人齐聚在杨府治水，更兼收救了许多灌口的难民，府中一时间倒也热闹。")]),v._v(" "),_("p",[v._v("可惜这对有的人来说并不友好，尤其对情窦初开的青年男女，人一多就碍眼，人一多就碍事。清晨龙女才找了个自以为无人的间隙要来杨戬的衣服浆洗，只在房间里闻到了情郎身上淡淡的松竹之气，面颊尤有晕红，立即被狐妹给撞破。")]),v._v(" "),_("p",[v._v("据狐妹交代，大家都瞧出来她喜欢杨戬，诚然这喜欢并不是什么丢人的事，寸心生来性格爽朗，喜欢便是喜欢，从不遮遮掩掩，可她也有些微的苦恼，怎么大家只说瞧出她的爱慕来，却没说瞧出杨戬怎样呢？")]),v._v(" "),_("p",[v._v("公主的爱是掺不得勉强的，倘使杨戬有一丝一毫的不欢喜，就算她再怎么倾慕他，也绝不会和他在一起。因此疑虑，所以有了误以为杨戬过敏而逃离之事，与为他带话约誓的四公主直言，因着这份倾慕，就算是他做了任何对不起她的事，无论大小，皆能谅解，但若未来的夫君对她身上的任何一点有厌，这亲也绝不会成，此为后话。")]),v._v(" "),_("p",[v._v("再说寸心与狐妹话别后，捧着杨戬的外袍到井边欲浆洗，却见此处聚满了来打水的人。她躲在柱后斗争了片刻，想到自己与杨戬尚无婚配，她施施然拿着一个男子的衣袍来洗，岂不是羞死人了？挣扎了片刻，又想到自己其实不会洗衣服，到时候恐怕又要被笑话，索性等天色晚些，人少些再来不迟。主意想定，即又抱着衣袍一溜小跑躲回去了。")]),v._v(" "),_("p",[v._v("杨戬本在演武堂边看哮天犬练拳，不一会儿觉得没意思，便至院中与杨婵一起安抚灌口借住的百姓。晚间大伙时有出去降妖挖渠的，而且人妖仙混杂，有的辟谷，有的还需米粮饱腹，是以府中并不摆宴席，只在院子里堆着一些汤饭，有热心的大婶们帮忙烧着，若饿了自去盛一些。有从小住在他家隔壁的婶子盛来汤饭与兄妹二人，他们也不便推脱，囫囵吃过了，只觉得盛夏燥热，饮食也无味。")]),v._v(" "),_("p",[v._v("杨戬在院中耽搁了整个下午，待回转演武堂时，已不见哮天犬与玉鼎真人的踪影。他心中暗暗记挂着拿了他衣衫去的罗裙女子，夏夜的萤火虫在暮色之中已经若隐若现，他呆呆地看了一会儿，只觉那荧光斑斓闪烁，仿佛他悸动不已的心声。回去也睡不着觉，不如在此练一练拳脚，捱过这个漫漫长夜，明天清晨就能见到她了。")]),v._v(" "),_("p",[v._v("主意议定，四周无人，他随手抄了一把长矛，在那台上演练起来，不一时已练得挥汗如雨。他又练了几套拳，换了几件兵器，因那兵器只是凡品，重量太轻，纵使手中汗湿，倒也无脱手之危，如此更多练了几个时辰，直到天色已晚，他身上的里衣中衣尽数汗湿了。")]),v._v(" "),_("p",[v._v("府中已经逐渐安静，这个时辰也基本不会有人再出来了，杨戬打完一套掌法，坐在演舞台的栏杆上，被夏夜的晚风一吹，只觉浑身粘腻难忍，心中那躁动不安的情绪倒是缓和了几分。演武堂到院后想也无人，索性将上衣脱下系在腰间，向后院的水井走去，想打水冲一冲凉。")]),v._v(" "),_("p",[v._v("走过九曲回廊，来至后院小径，四周安安静静的，杨戬忽然驻足在桥头，只见月色之下，一个红衣女子正半倚在井边，仔细搓洗一件黑色的男子外袍。")]),v._v(" "),_("p",[v._v("她身形窈窕，一张小脸因劳作透出淡淡的绯红，不是他惦念了一整天的女子是谁？杨戬呆呆地看了一会儿，见她双指并起自井中御水而上，浇在那件袍服上冲洗皂荚，一双细嫩的小手拿起衣角认认真真地搓洗，动作十分生疏，很快将双手搓红了。")]),v._v(" "),_("p"),v._v(" "),_("p",[v._v("杨戬见状，顿感一阵心疼，忍不住走上前说道：“三公主，还是我来吧。”")]),v._v(" "),_("p",[v._v("寸心被他吓了一跳，打眼看他赤膊而立，瞬间红了耳根，轻呼一声撇过头去，讶然道：“你怎么光着膀子呀！”")]),v._v(" "),_("p",[v._v("杨戬闻言，也是面上一热，“抱歉……我以为没有人了……”偷眼见她粉腮嫣红，有如蜜桃，心中不由得暗暗欣怡，忍不住出口逗她：“寸心……此处便只是你我二人，有，有什么要紧？”")]),v._v(" "),_("p",[v._v("“呸，”寸心闻言，回头来啐他一口，“不知羞，你混说什么呢？”一抬头，正撞上他一双含笑的星眸，心中霎时也觉得甜蜜不已。盛夏夜中，年轻的男女难得地独处，繁星满天，心心相印，皆是悸动不已。")]),v._v(" "),_("p",[v._v("龙女手中揉搓着他的衣服，稍稍低下头去避开他灼热的视线，以免脸颊愈发红润，被他发觉，那可羞死人啦，“我，我不太会洗，但，但你放心，我会好好学的……”")]),v._v(" "),_("p",[v._v("杨戬心中一动，在她身边找了个石台坐下，笑道：“我放心什么？”")]),v._v(" "),_("p",[v._v("“就是放心……”寸心话未说完，忽然瞥见他唇边压不住的笑意，当即反应过来他这蔫儿坏的是在逗人，气急败坏地一拳捶在他臂膀上，柳眉倒竖，“喂！”")]),v._v(" "),_("p",[v._v("杨戬哈哈大笑，受她小拳头不轻不重地擂了几下，心中只觉甚为熨贴。这院子甚为宽广，竖井旁有一荷花池，正值炎夏，荷花盛放，杨戬与她坐了一会儿，见她老是盯着池子里的荷花看，于是问道：“寸心，你喜欢吗？”")]),v._v(" "),_("p",[v._v("龙女支着脑袋，微微笑道：“是啊，海中没有荷花，我看着甚为新奇。”")]),v._v(" "),_("p",[v._v("“你等着，我去摘一支给你！”杨戬一撩系在腰间繁重的衣物，正欲起身，却教龙女一把拉了回来，“哎，不必折腾了，”她笑吟吟地看着他，“瞧你，浑身都是汗，我给你舀水冲一冲吧。”")]),v._v(" "),_("p",[v._v("夏夜燥热，寸心取了随身的巾帕为他浅浅擦干肩膀上的水珠，穿堂的风一吹，身上也干了七八分。这时院中虫鸣阵阵，微风和煦，两人站在水井旁，寸心抬头看他，只见他眼中有熠熠星光。")]),v._v(" "),_("p",[v._v("她忽然伸手穿过杨戬的臂弯，轻轻靠在他的胸膛上，抱着他晃啊晃，软声说道：“郎君，往后我们在一起，我天天替你浆洗衣物，伺候你饮食起居，你说好不好？”")]),v._v(" "),_("p",[v._v("杨戬被她这样一抱，一时间温香软玉满怀，受宠若惊，双手竟不知落在何处，犹豫了半晌，局促地落在她腰间，耳中只听她说“往后在一起”之言，心中已经暗暗想到他们二人相守一生的甜蜜光景，不由得惊喜万分，“好，好，寸心，咱们一辈子不分开！”")]),v._v(" "),_("p",[v._v("至于什么伺候起居之言，一个也就随口一说，一个也就随耳一听，倒是谁也没放在心上，此为后话。")]),v._v(" "),_("p",[v._v("杨戬这厢被这心尖上万般在意的少女抱住，一低头，只见她粉面桃腮，兀自还因方才胆大脱口的海誓山盟羞怯，鸦羽似的睫毛忽闪忽闪地，几乎蹭到他胸前的肌肤上，他心中暗流涌动，柔情绰绰不止，拇指轻轻摩挲着她的衣带，温声道：“寸心，我能……我能亲亲你吗？”")]),v._v(" "),_("p",[v._v("龙女闻言一惊，猛的抬头看他，又嗖地一声缩回去，杨戬见她耳根慢慢地红透了，半晌听到她闷闷地说道：“嗯……”")]),v._v(" "),_("p",[v._v("杨戬心中一喜，轻轻扶住她脑袋，趁着这夜色撩人，郎情妾意之景，吻住她的唇。")]),v._v(" "),_("p",[v._v("唇齿间触碰缠绵如卷袭旱原的烈火一般无可收拾，杨戬只觉她的唇异常柔软甘甜，鼻息间满是甜腻的香味，想将她抱紧一点，再吻深一些，也好平复心中灼灼燃烧的爱意。他本能地探出舌去，龙女软糯乖巧，怎么招架得住？轻易就教人侵入了牙关，他含吸住她的香舌，霎时将人亲得浑身发软，堪堪依靠在他怀中，润玉一般的指节微扣在他肌骨上，指尖微微泛白。")]),v._v(" "),_("p",[v._v("动情的儿郎如何招架得住心上人如此娇糯之态，一面亲她的小嘴，忍不住摩挲着她的腰肢，吻得头脑中晕乎乎地，本能地滑到她臀肉上摸了几把。")]),v._v(" "),_("p",[v._v("寸心嗯了一声，少女特有的警觉令她从热吻之中回过神来，瞪圆了一双杏眼使劲推开他，撅着被他吮得红润的小嘴嗔道：“你干嘛呀！”")]),v._v(" "),_("p",[v._v("杨戬尤为不愿地被她推开，气息也有些不稳，看着她软嫩的双唇咽了一口津液，柔声道：“我错了寸心，我错了。”")]),v._v(" "),_("p",[v._v("“哼，”寸心心中暗骂他呆子，有些恼他孟浪，一跺脚就欲跑，“我走了，你自己洗衣服吧！”")]),v._v(" "),_("p",[v._v("“欸，”杨戬连忙拉住她，“好殿下，我错了，你别走，咱们再待一会儿罢。”瞧见龙女犹豫地看他一眼，他献殷勤似的捏捏她的小指，倘若身后有条尾巴，一定摇得很欢实，“再待一会儿，好吗？”")]),v._v(" "),_("p",[v._v("龙女轻轻哼了一声，心中其实也极为愿意同情郎腻在一起，小声道：“好吧。”由着他牵着，坐回井旁，二人又凑到一起，说起悄悄话来。")]),v._v(" "),_("p",[v._v("再说她之后偶或吃了飞醋，闹着问他是否对旁人也如此这般，究竟是如何的如此这般，也就只有他二人心知了。")])])}),[],!1,null,null,null);_.default=n.exports}}]);