(window.webpackJsonp=window.webpackJsonp||[]).push([[227],{593:function(n,r,t){"use strict";t.r(r);var b=t(4),v=Object(b.a)({},(function(){var n=this,r=n._self._c;return r("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[r("p",[n._v("自打那日杨戬口不择言地在娇妻和爱犬之间做了个错误选择，他已经有半月没回得房了，倒不是真能狠得下心冷落美娇娘，实在是公主殿下气性大，将他的枕头被褥一股脑儿地丢了出去，堂堂二郎真君也只落得个独守空房的下场。"),r("br"),n._v("\n这日，杨戬照例在亭中煮茶，同往日一般注意着厢房的动静。敖寸心大半日都未出房门，他有些分神，一连往壶中添了两次茶叶，水咕嘟咕嘟地煮开，冲到杯中，清苦的气味四散开来，引得身旁小犬接连打了两个喷嚏。杨戬心思却浑然不在这儿，他低头向狗儿问了一句：“今天什么日子了？”"),r("br"),n._v(" "),n._v("\n“二月初八，主人。”哮天犬恭恭敬敬回了一声儿，又眼巴巴瞧着壶中还在沸腾的泉水去了。"),r("br"),n._v("\n二月初八……坏了！"),r("br"),n._v("\n杨戬心神一震，顾不上其他，起身径直往厢房去了。"),r("br"),n._v("\n“诶主人……”哮天犬在后头担心，万一凶巴巴地三公主又和主人吵起来怎么办？"),r("br"),n._v("\n待行至门前，杨戬略一犹豫，却听到屋内有细碎的哼叫，便再难生出什么旁的心思，推开门走了进去。"),r("br"),n._v("\n屋内仿佛比外头热些，甚至有隐约的雾气，敖寸心皱着一张通红的小脸缩在床里，时不时溢出甜腻的呻吟。"),r("br"),n._v("\n果然是到了思春期了。"),r("br"),n._v("\n杨戬三两步上前，伸手抚上她蒸得红扑扑的脸颊，低声唤道：“寸心？”"),r("br"),n._v("\n粗砺的大手微凉，敖寸心乖顺地在他掌心蹭了蹭，旋即睁眼，瞧见来人是她夫君，立时委屈道：“杨戬，我难受。”因着情欲折磨，她的声音愈发婉转娇媚，听得杨戬脊骨升起一阵酥麻，他耐着性子去哄：“乖，我在这，没事的啊。”"),r("br"),n._v("\n说罢腾出手来除去束带脱下外衣，躺到敖寸心身边将人搂进了怀里。依着往日的习惯亲亲她额头，再将肩头素色薄纱褪去，此刻小妇人却嫌他太慢，哼唧着去扯他的里衣，动作愈加放荡，掀起长裙跨坐在腰腹间，隔着衣物去蹭他胯间那根硬物。来之不易的抚慰让敖寸心难以自持，双手撑在杨戬胸前，夹紧了穴口来回磨蹭，春水四溢打湿了他的衣物，呻吟间夹杂着着粘腻的水声。"),r("br"),n._v("\n杨戬叫她蹭得火起，伸手探进裙摆寻见充血发硬的花核，拇指重重碾上，引得身上人一阵战栗，私处涌出一大包水来。他翻身，扯下那条已经洇透的裤子，扶着肉棒在穴口戳弄几下，那水光潋滟的小嘴儿立刻啜吸起来，敖寸心痒得难受，自己抬了腰去够，杨戬轻笑着摸上她耳垂，沉腰到底，两人皆是发出一声满足的喟叹。她内里温热湿滑，这会儿又被他喂得舒意，软肉不断绞缩吸附，快意上头，杨戬掐着她纤腰，大开大合间使足了力气。敖寸心叫他顶弄得语不成声，素手揪住床幔，绷紧身子去了一遭。"),r("br"),n._v("\n此刻杨戬缓缓退出，伸手在她嫩肉处肆意揉捏，灭顶的快感还未退去，被他这么一刺激，敖寸心登时哭叫着喷出水来。杨戬将人翻转跪趴在榻上，就着湿意再次捣了进去，这下入得极深，敖寸心腰腹一颤软软的就要往下倒去，却被杨戬伸手从胸前环住，强迫着立起身来，牙齿轻轻磨着她颈边皮肉，笑着问道：“这会儿可好些了？”说罢另一手抚上她胸前绵软，腰胯复又顶弄起来。敖寸心这会子已然不知今夕何夕，只随着杨戬抽插的频率泻出破碎的呻吟，身下泛滥的春水甚至顺着两人大腿滑落，穴肉连带着小腹强烈痉挛。在这致命的绞缩中，杨戬加快了速度，臀肉相击的声响混着水声，一室淫靡。粘稠的浓精随着激烈的撞击喷薄而出，一股一股地打在花心上，敖寸心身子一软伏在床上，嫣红的花穴还在抽搐，精液混合着淫水往外溢。"),r("br"),n._v("\n杨戬随手扯了条帕子过来给她擦拭，甫一触上小穴，她便颤抖得更厉害了，哼着声说不要。可眼前这情状实在淫乱，翕动的穴肉含不住精液，素白的寢衣堆在了腰间，发丝凌乱地粘在额前，敖寸心还在张着嘴喘息。仅仅是看着，胯间那条半软的物什又抬起头来，杨戬怜惜地拨开她脸侧的发丝，亲亲她嘴角，语气暧昧又温柔：“缓过来了吗？我们继续？”")])])}),[],!1,null,null,null);r.default=v.exports}}]);