(window.webpackJsonp=window.webpackJsonp||[]).push([[295],{665:function(_,v,p){"use strict";p.r(v);var t=p(5),n=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("夏虫蛐蛐叽叽喳喳吵得正欢，夜色澄澈如水，今日天上的明月分外明亮，一阵清凉的风挂刮过，惹得梧桐树上琳琅满目的竹制风铃相互敲击，发出清脆悦耳的响声。")]),_._v(" "),v("p",[_._v("东厢房中有檀香缓缓从消燃的金兽中飘散开，整个屋子充斥着清冽的翠竹香气。书册被褥整整齐齐码在床头案板上，连玄龟镇尺下的宣纸也丝毫不随窗口风动。厢房的主人剑眉星目、白衣胜雪、环佩羽冠，盘腿坐于榻上入定，沉蕴内敛而不可逼视。")]),_._v(" "),v("p",[_._v("“大道无形，生育天地；大道无情，运行日月；大道无名，长养万物；吾不知其名，强名曰道。夫道者：有清有浊，有动有静；天清地浊，天动地静。男清女浊，男动女静。降本流末，而生万物。清者浊之源，动者静之基。人能常清静，天地悉皆归。夫人神好清，而心扰之；人心好静，而欲牵之……唉……”")]),_._v(" "),v("p",[_._v("清心咒颂至泰半，天神轻轻叹了口气。屋外飘来悠悠的香味，无端勾起肚里的馋虫，满室檀香弥漫都阻隔不了自小院而来源源不断的干扰。")]),_._v(" "),v("p",[_._v("这定是再如何入不成了。")]),_._v(" "),v("p",[_._v("神君细细嗅了嗅，辨别出是糕点软糯鲜甜的香气，想来亦应当别有一番风味，只是刻下不晓得有没有这个口福可尝上半小块。夫人今日同他发了脾气，夜半不睡特意烧出几道格外香喷的小食，想来定然是有八分故意在其中，两分赌气在其表。")]),_._v(" "),v("p",[_._v("不晓得今夜的夜宵为何无端这样吸引人，杨戬微微睁开闭得有些酸痛的阙庭一目，伸出纤长两指轻轻揉了揉，恍恍惚惚地想，这个软，他是服，还是不服？")]),_._v(" "),v("p",[_._v("香气愈发浓烈，萦绕在他的鼻尖，将人混沌的思绪搅和得更加混沌。神君尚未决定是否要先道这个歉，身体已经自觉地打开厢房的阁门，向香味飘来的地方踱步过去。")]),_._v(" "),v("p",[_._v("杨戬的府邸甚为宽敞，有时神君同夫人吵了架，彼此均有意避而不见之时，同在一个屋檐下闲逛游荡，统共能有小半日都碰不到对方。盖因夫人出阁之前惯喜佐料小食，烹饪之法，以至于厨艺十分之高超。若杨家二郎某日有幸哄得夫人展颜，则其亲自下厨为心上人烹饪两道下酒小菜，亦不失为郎君神生间一桩美事。")]),_._v(" "),v("p",[_._v("后花园中有一处甚为清幽的小潭，潭边种满了不知是哪一个年月采办回来的桃花树，昔日由夫妻二人亲手栽下，如今已经长得枝繁叶茂，硕果累累。半垂在潭上九曲回廊间的湖心亭畔，遮掩住亭中影影瞳瞳的烛光。杨戬微微眯起眼，透过重重的青绿枝桠，瞧见亭中一个披头散发的白衣龙女，肤如凝脂，眼波流转，抬起小半截白玉般的手臂，摘下低垂至亭中小枝上的一颗青桃，拿手边的竹木夹取，轻轻扔进温煮在爵器之中的清酒里。")]),_._v(" "),v("p",[_._v("青桃之上尚有一层细细的绒毛，融入酒水中，泛出一股清新淡涩的味道，随着酒香缓缓蔓延在湿润的空气中。杨戬一身白衣道袍清风朗月，缓缓举步走上九曲回廊，朗声笑道：“世人只知青梅煮酒，却不晓得青桃煮酒，会是什么滋味？”")]),_._v(" "),v("p",[_._v("亭中少女闻言，缓缓抬起头来。她身下垫着厚厚的长绒毛毯，满亭温暖的烛光，将她的身形镀上一层温暖的颜色。")]),_._v(" "),v("p",[_._v("龙女打眼一瞥几步之外的道袍青年，声音不辨喜怒：“那你说，青桃煮酒，会不会好喝呢？”")]),_._v(" "),v("p",[_._v("郎君摇了摇头，缓缓走进亭中，言道：“若要我说，定然又酸又涩，不会好喝。”正说着，他褪了鞋袜，盘腿坐于龙女身畔，伸手正欲捻起她坐前半余的酒杯，行至一半被一双温温软软的小手拍了下去。敖寸心微皱着眉，一身白色睡袍衬得她肤色愈发红润，正睁着一双水汪汪的眼睛瞪他，轻骂：“那你别喝！”")]),_._v(" "),v("p",[_._v("“怎说，”杨戬转而捻起她盏里的小糕，不顾她张牙舞爪的阻止放到嘴里，然后把她稳稳地揽住，“我又不是专程来喝你的酒，只不过来吃你的糕。酒难喝些，亦是无妨。”")]),_._v(" "),v("p",[_._v("寸心受不住他凑到耳边的低沉嗓音，杨戬身上的清竹檀香味道太浓，一时间充斥了她的整个鼻腔，将脑袋也熏得嗡嗡地。她勉强抬手不轻不重地捶了一下他的胸膛，声音糯糯地却想要显得自己凶一些：“谁叫你吃了？不许吃我的糕点！”")]),_._v(" "),v("p",[_._v("“你不能这样……”杨戬缓缓摩挲着她软软的肩头，“倘若不让我吃，我一定会很伤心的……”")]),_._v(" "),v("p",[_._v("寸心倔强地拧出他怀里，撑着他的肩膀转过身来同他平视时，恰逢杨戬软乎乎地贴过来，将脸埋进她的肩窝里。")]),_._v(" "),v("p",[_._v("龙女嘟着嘴，却没再将他推开。夜色如水，郎君的华服同龙女的寝衣融为一体，发冠轻轻带跑了她的几缕青丝。少顷，杨戬嗤地一声笑了。")]),_._v(" "),v("p",[_._v("“寸心……”")]),_._v(" "),v("p",[_._v("“嗯？”")]),_._v(" "),v("p",[_._v("杨戬抱紧了她：“让我尝一尝你的酒罢……”")]),_._v(" "),v("hr"),_._v(" "),v("p",[_._v("【晨】：采访一下真君老爷，所以那杯青桃酒到底好不好喝呢？"),v("br"),_._v("\n【杨戬】：啧 (ー_ー)")])])}),[],!1,null,null,null);v.default=n.exports}}]);