(window.webpackJsonp=window.webpackJsonp||[]).push([[310],{676:function(v,_,p){"use strict";p.r(_);var t=p(4),n=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("p",[v._v("红日哀哀将尽，暮霭沉沉漫上灰白的院墙，偌大川主府邸死寂无声，仆役皆是噤若寒蝉，满地碎瓦残片也无人敢拾掇一二，踹桌摔碗的碰碰声终是消弭，想来狂怒的神龙也有精疲力竭之时。")]),v._v(" "),_("p",[v._v("敖寸心环抱双膝坐于中庭露台阶上，她本瘦小这会安静的缩成一团好似柔顺的绒线娃娃。可杨戬晓得她是如何自这糯小的皮囊下伸出劈金断铁的爪牙，撕人肺腑碎人心门，又是如何生出恶毒尖刻的心思将呱呱幺子弃之荒野。")]),v._v(" "),_("p",[v._v("如此这般再见她戚哀之态，杨戬再难生甚么怜爱，心下烦郁更甚端觉龙女寡情薄义不可理喻，是以他只嘱咐两句家犬便提刀而去。")]),v._v(" "),_("p",[v._v("打杨戬一进府门，敖寸心便已知晓，她怨这人无端猜忌又叫合离之事闹得惊慌，砸锅摔碗的泄愤一阵，本已冷静些正想上前辩白义女遗失之事。")]),v._v(" "),_("p",[v._v("却见杨戬厌她如草芥，端觉叫他拿捏过的咽喉隐隐作痛，这四阖高耸的院墙愈发紧缩迫得她口鼻难喘，好似搁浅的池鱼周身具是粗粝的泥沙，一寸寸剜着她的皮肉苟延残喘生不如死。")]),v._v(" "),_("p",[v._v("待到肩头传来温热，“寸心”又闻四姐柔声唤她闺名，龙女只觉终是等得自己人，腹中委屈好似潮水涨满心扉，正要哭怨两句便听她这好姐姐道“我们回西海罢。”")]),v._v(" "),_("p",[v._v("敖寸心闻言一怔瞧着龙四分外莫名，听心唇齿开阖犹疑片刻，似见她不为所动便又道“婵儿与我皆觉嫦娥所言在理，纠缠倘若只余苦痛便且放手罢，你乃叔婶亲女日子总也好过的……”")]),v._v(" "),_("p",[v._v("龙女只觉嫦娥两字在她耳畔炸响，震得她脑内轰鸣，心下只道：她终是耐不住了！那杨戬呢？他又作何想？！")]),v._v(" "),_("p",[v._v("她叫这念头关进泥浆封了口的瓦瓮，架上汹汹烈火，四处碰壁无路可出，心中抑郁不解直要活烤成汤，她再坐不住腾得起身往庭中水榭胡窜去。")]),v._v(" "),_("p",[v._v("敖听心见了只当她要寻杨戬说论一二，他俩夫妻事她虽顶着娘家人的名头却也不好再进，便由她去等了良久她又不放心才往里去寻……")]),v._v(" "),_("p",[v._v("一步错步步错，敖听心怎的也料不到她今日所为将这岌岌可危的杨家，一下摔作稀碎。")]),v._v(" "),_("p",[v._v("人道是不到黄河心不死，不见棺材不落泪，敖寸心撑着河畔柳树大口大口的喘气儿，堪堪缓过脚下一动便要问个明白，一打眼只见水光盈盈河草葳蕤，空荡荡哪有甚么千八百年为表心意弃去的新婚贺礼。")]),v._v(" "),_("p",[v._v("她曾执拗地将满腔情义自己坚贞半生全数押在这湖底月饼之上，然而潺潺流水满榭游鱼皆在笑她荒唐，龙女心肝碰碰直跳，宛若失了刹闸的马车飞驰着愈来愈快，最后狠狠撞上无情的磐石，轰然一声残破四碎。")]),v._v(" "),_("p",[v._v("失望、悲伤、委屈百般心绪皆成汹汹怨火，腹中毒蛇裹得脏腑剧痛，它吐着殷红蛇信子绵绵低语：玉石俱焚！玉石俱焚！")]),v._v(" "),_("p",[v._v("锵得一声响，凌霜剑映着月色寒光冽冽，敖寸心湖蓝衣袖一振扶摇上九天。")]),v._v(" "),_("p",[v._v("嫦娥身担鸾台监察之职，正于月宫俯望门下集纳各方耳报，忽觉身后罡气呼啸，猛地拧身闪过正欲斥何人妄为擅闯资情重地，却见敖寸心双眼猩红提剑便刺。")]),v._v(" "),_("p",[v._v("她以弱质女流之身‘孤立’朝堂万年不倒，探查人心之事信手拈来，刻下见龙女搏命之态一目了然，自无惊慌只抓猫逗狗般的左右闪避，暗地松口气儿道所奉皇命将成。")]),v._v(" "),_("p",[v._v("未曾想这千百年不提刀挥剑的川主夫人，却是个难缠主，步伐无章刀剑无眼只卯足气劲儿往她面门划刺，呼号的龙气也迫得人难挨，端是杀不得伤不了也要花了她脸面的派头。")]),v._v(" "),_("p",[v._v("剑气风声倏忽荡过耳畔，带下月宫仙子一缕青丝，泥人尚有三分性，她顾念龙女娘家门阀夫君权势一再忍让这会却也闹了脾气。")]),v._v(" "),_("p",[v._v("嫦娥手掌一翻将龙女手腕拿下，自关节施力迫她弯折，又往前一送逼她后撤，却哪想本闷头胡砍的龙女忽的扬起头，咧嘴显出放肆笑意和着金色竖瞳中狂乱眸光，面容狰狞吊诡，脚下一蹬迎着剑头撞来，嫦娥心下惊呼：遭了！")]),v._v(" "),_("p",[v._v("噗的轻响，嫦娥只觉手下薄剑微微一顿，便势不可挡的急驰而去她奋力后撤却是无用功，定睛回神竟是龙女空着的手，正死死的握着剑身叫她半分难动。")]),v._v(" "),_("p",[v._v("敖寸心咧着嘴白皙的贝齿细小的舌具是鲜红，她却咯咯轻笑磕磕绊绊道“我……也叫……你、你……日夜……寝食……难、难……安”。")]),v._v(" "),_("p",[v._v("嫦娥虚长她千百岁，却从未干过杀人取命的勾当，又叫她诡异狞色唬地惊慌，一心只想离她远些抬手便往龙女肩头全力一掌。")]),v._v(" "),_("p",[v._v("削铁如泥的宝剑凌霜，自她前心当胸而出，终叫积压她心头的重山荡然无存，骨肉剧痛失血叫龙女双目迷蒙，可她竟觉无与伦比的畅快。")]),v._v(" "),_("p",[v._v("终于高耸巍峨的杨府院墙塌了，终于求不得恨离别的藩篱塌了，终于狭小逼仄的瓦瓮碎作齑粉，她荡起龙尾便可奔赴海阔天空，这远处是熠熠生辉的光明前路，她在心下真臂高呼：自由！自由！")]),v._v(" "),_("p",[v._v("杨戬本自下届见朗月阴影幢幢，神目一开眼见敖寸心持械逞凶，她如此打上门去无异宣告天下，他杨戬朝情慕楚与嫦娥有染，直如当众狠狠甩了他一耳光，更兼寻衅朝臣之罪简直愚蠢之极！不知所谓！")]),v._v(" "),_("p",[v._v("杨戬踏云急去恼怒间早将龙四提议暂缓合离之事抛之脑后，满心皆道：如此蛮横无理之徒走了才是干净！")]),v._v(" "),_("p",[v._v("哪里想赶得月宫，竟眼见仙子一掌将敖寸心击飞，风裹挟她单薄的身躯飞振倒来，“寸心！”杨戬失声高呼，着急慌忙便去接，咚！他的膝盖重重地磕在冰凉的石砖上。")]),v._v(" "),_("p",[v._v("他如何料到接到怀中的敖寸心是这般光景，她身前血迹早将衣裙糊变了色，那心口却仍源源不断的渗血，眼睑打颤面上血色退却将将灰败，眼尾额角是干瘪的龙鳞，竟是个重创下的将死之人。")]),v._v(" "),_("p",[v._v("杨戬猛地抬头瞧向嫦娥，但见她竟尚提着凌霜，这龙女两百正寿的生辰礼，饮饱了主子的鲜血，在嫦娥纤长白皙的手下闪着妖冶寒光。")]),v._v(" "),_("p",[v._v("嫦娥眼见杨戬双瞳倏忽猩红，腮骨紧绷杀气腾腾的样子恰如当年她拦在十殿下身前所见，不是的！是她自寻死路！")]),v._v(" "),_("p",[v._v("她开口欲辩，却见杨戬蹭地起身一手揽着龙女，一手护着她的心门，对着匆匆赶来的哮天喝道“取了凌霜，唤三妹速速回府！”见哮天愣着又大喝道“去啊！”")]),v._v(" "),_("p",[v._v("孙悟空几人受邀来得杨府共度中秋佳节，堪堪来得府中便闻一言“杨戬，我三妹……”话声未歇就见呼啦涌来一群人，领头的正是东海娇客敖听心。")]),v._v(" "),_("p",[v._v("龙四见着他们这行，立时禁了声踌躇地退后一步，却叫他寡言师弟拦了路“听心，三妹怎的了？”龙四闻言面色骤变双眼睁得恁大，好似活见了鬼支支吾吾半晌说不出个囫囵话。")]),v._v(" "),_("p",[v._v("这回孙悟空才真晓得玉龙太子到底如何表个‘烈’字，“说话！”小白龙大喝一声，激得龙四浑身一颤，慌张道“你凶我作甚！我难道不急嘛!他俩不知怎的闹祸……”敖听心说着四下一瞧抿了抿嘴复道“一早出了门，这会也没回！”")]),v._v(" "),_("p",[v._v("敖烈闻言一怔言道“他俩一程，天塌了也有杨戬顶着，你慌什么？”他说着面色一沉蹙眉疾言“他俩动手了？！”")]),v._v(" "),_("p",[v._v("孙悟空是个人精见龙四面露难色，忖内情不便外道即打圆场道“师弟关心则乱，杨小圣甚么人品何况他俩夫妻恩重？”")]),v._v(" "),_("p",[v._v("姚公麟也接口道“正是，俩位殿下莫慌，二爷夫人须臾便回了。”这梅山老四话方歇，立见流光一过，正是仁佑王显圣真君回返。")]),v._v(" "),_("p",[v._v("天地千万里，杨戬行路风驰电掣，运功不歇可这血好似开了闸的洪水，将他半襟衣物染得湿了一层又一层，他落得中庭便连回房的功夫都不敢耽搁，立时盘坐调动九转玄功，切切促动龙女剑伤愈合。")]),v._v(" "),_("p",[v._v("血终是止了，敖寸心周身薄凉，瞧着就要阖眼，杨戬紧搂着她，顾不得满手血污，慌忙抬手轻拍她的脸颊，口中喋喋道“寸心！寸心，别睡！你听话，三妹就到了！”")]),v._v(" "),_("p",[v._v("龙女气息愈发浅，眼睑到底沉沉阖了，杨戬端觉心口叫人踹了脚，气倒也倒不过，“百般是我不好，你生气咱闹也成，换个法子罢……寸心你别吓我……”")]),v._v(" "),_("p",[v._v("杨戬瞧着敖寸心悄无声息，端觉一阵阵生凉，几欲软了手脚他强撑着口气，抬头厉声高喝“愣甚么！张仲景呢！等他爬了来陪葬吗？！”")]),v._v(" "),_("p",[v._v("俩位龙子先时眼见着敖寸心一身血污，也是慌乱无措更兼杨戬神法通天，料是在他身旁才最是安妥，这会竟闻他论生道死，敖烈又气又急跨前一步便要将敖寸心抱了走。")]),v._v(" "),_("p",[v._v("咔——“阿烈！”听心确信敖烈手骨已裂，着急慌忙的要上前拉他，却正受上杨戬一记眼刀，但见他睚眦殷红，额角脖颈青筋迸张，周身煞气凌冽，大有谁敢往前便拔刀相向之态。")]),v._v(" "),_("p",[v._v("敖烈玉面煞白鬓角疼得淌汗，却仍是不惧咬牙切齿道“她是我妹妹！”话落杨戬五指却收得愈发紧，小白龙惯用手泛起青白只怕要废在此处，孙悟空见了急得抓耳挠腮，提棒上前便要帮腔。")]),v._v(" "),_("p",[v._v("“来俩个砍一双！道是你棒快，还是我刀利！”话落孙悟空师兄弟与梅山众人顷刻剑拔弩张，唐僧立马合眼并手叠声不歇念起佛号，敖听心急得跳脚，玉鼎蒲扇摇得飞快切切言道“别叨叨了！都放下！都放下！徒儿这可是你大舅子，自家人啊！”")]),v._v(" "),_("p",[v._v("猪刚鬣闻言心下直呼：哎呦喂，哪壶不开提哪壶！果然杨戬立时接口道“哪来的自家人！敖寸心生是我的人，死了那也是我亡妻！带她走，你西海想都不要想！”")]),v._v(" "),_("p",[v._v("敖烈气得七窍生烟，左手顷刻成爪兜头便打，千钧一发之际一抹烟绿宝光盈盈罩来，敖烈龙爪杨戬长刀具叫其轻轻一格，敖寸心灰败面色亦也红润起来。")]),v._v(" "),_("p",[v._v("“二哥！莫说气话！”来者一身鹅黄千水裙，正祭了莲灯至宝为敖寸心疗伤，她转脸对着敖烈微微颔首温声道“我二哥关心则乱，太子见谅”光华过处敖烈腕骨恢复如新，杨戬煞气也叫压制，随即收了势。")]),v._v(" "),_("p",[v._v("一干人等具退一步，敖烈锁紧眉头仍是不忿，却叫敖听心扯了袖子“你耐着些罢，好歹叫宝莲灯试试，不然……”敖烈回首见她泫然欲泣，便也落牙和血吞了。")]),v._v(" "),_("p",[v._v("“杨二哥！我将这老倌带来了。”哪吒擒着张仲景衣衫后领，将这巡诊蜀地的医官拽回了杨府，随后喘气不止的直健与托塔天王也一并到了。")]),v._v(" "),_("p",[v._v("老六正要回禀却见康安裕打了眼色，便老实闭了嘴，而与他半道相逢的李靖瞧着杨戬暗沉脸色，又掂量手中圣旨正踌躇着，便叫小子一拽“父王可别往枪口上撞！”这父子俩正论长短，那哮天犬凑上前来“这宣得甚么旨意？”")]),v._v(" "),_("p",[v._v("这厢李靖左右为难，那头张仲景日子也很难过，他顾不得打颤的腿肚，来不及喘口气儿，便疾行奔到真君近处跪下口道“学生来迟”正要行礼，却闻杨戬冷声道“少耽搁，瞧坏了仔细你的皮！”")]),v._v(" "),_("p",[v._v("医官口中叠声应是，膝行两步搭上龙女腕脉，瞧着瞧着便已汗湿重衫，他将龙女手腕呈回杨戬掌中，膝行退后些许以额重重磕地颤声道“学生无能，但请王爷责罚！”")]),v._v(" "),_("p",[v._v("话声落下这院内好似叫人下了咒，将一干人等具定了身，张仲景端觉叫这寂静扼住了咽喉，气也不敢喘，半晌才闻杨婵道“先生可要仔细！嫂子外伤已愈，兼有莲灯至宝在此，如何便不行了！”")]),v._v(" "),_("p",[v._v("张仲景又是一磕，回话道“心乃脏腑中枢，叫利器横穿生劈……学生实无通天逆命之能啊！”这厢话落，敖烈再难自持脱口骂道“我三妹招得甚么孽障！叫你这命硬的混种活活克死！”")]),v._v(" "),_("p",[v._v("哮天犬历来护主，立时吠呼道“你敢骂我主人！”得亏直健拽着不然只怕这会已然动了手，他自是不服高呼道“你放开！三公主自己凭白发颠与嫦娥仙子搏命，自己本事不济，怪得了谁！”")]),v._v(" "),_("p",[v._v("敖烈闻言一怔，顷刻气得鬓发直竖，口不择言“三心二意的混账！你到底将我亲妹往死路逼嘛！”说着便要上前与杨戬搏命。")]),v._v(" "),_("p",[v._v("敖听心拦他不住，而杨戬已然宛若死人，万般心焦无措，抬手狠狠给了敖烈一巴掌，紧紧抓着他的衣襟声嘶力竭道“你便怨我罢！是我糊涂代嫦娥传了叫她另谋良人的话，你便打我罢！”")]),v._v(" "),_("p",[v._v("敖听心说着便软软地滑下身去泣不成声，敖烈怔了半晌大笑出声又是泪泗横流悲声道“为着那恬不知耻的寡妇贱人，我这一气儿要折去多少姊妹，可笑！可笑！”")]),v._v(" "),_("p",[v._v("此间万般争闹不休，杨戬也半分不顾，只抱着敖寸心恍然出神，念起往日种种，自打相逢起便叫她搭救一程兼一程，少年夫妻至此敖寸心嬉笑怒骂蹉跎韶华，伴他走了累累半生。")]),v._v(" "),_("p",[v._v("可笑末了闹得相看两厌，此前他还满心念着离了她这狭心小气的蛮横主，图个干净安宁，可是……可是……人气急之下总是糊涂，我断不是这个意思，寸心你明白嘛？老天你明白吗？")]),v._v(" "),_("p",[v._v("他不觉念起相敬如宾的父母，念起往日信誓旦旦相守一生之言，哪里料到敖寸心的一生竟这等短，堪堪不过一千五百载，他到底哪里出了错，落得这等妻亡家破的田地“不若你教教我……呐，寸心你教教我罢，我该如何做，如何做……”")]),v._v(" "),_("p",[v._v("杨戬抱着敖寸心茫然无措，半晌喃喃道“戬字曰福却叫我一生喜乐剪灭荡然无存，杨戬命硬在理，十成在理！”他说着不知忽的念起甚么，猛地抬头面上风霜痕迹仍新，哑声道“若换心呢？”")]),v._v(" "),_("p",[v._v("三十年河东三十年河西，今年风水到杨家，天界诸神也爱饶舌，私下里总要传两句新晋司法天神的闲话，说人福厚罪罚之身一朝显达，道人命苦爱妻撒手人寰，也论人手腕通天舍了糟糠与美眷近水楼台……")]),v._v(" "),_("p",[v._v("杨戬只当天界风大充耳不闻，日日埋首案牍宵衣旰食，他信步取道天河，正往司法神殿去，忽闻娇声唤道“杨戬！”脚下一顿，复又急行而走，“真君身兼司法重责，竟连忠事求实也做不到吗？！”")]),v._v(" "),_("p",[v._v("嫦娥见他歇了脚，只当说动便又复言“雷部参我监察不利，漏报八公主滞留凡尘，竟要我吃天雷挂落，撤我少使之职，他们如此不过敖侍郎与我私怨不休，以公相谋！天神……”")]),v._v(" "),_("p",[v._v("“与本君何干？！”杨戬侧首答话，声色分外生硬，嫦娥不妨他这等寡淡，叫他堵的一滞心生火气复道“我早言爱欲不同，三公主不堪此道与我拔剑相向！”她言说间见杨戬仍不为所动，不由暗恨话道“乃至蓄意身犯神兵之利，如此这等到底怨谁？！”")]),v._v(" "),_("p",[v._v("她堪堪话落，杨戬便猛地拧过身来，面结寒霜沉声低喝“饶是你多事！我敬你三分为长，你倒手长起来，我瞧着可似痴傻，真当我不知你奉何命承何恩？！”")]),v._v(" "),_("p",[v._v("嫦娥叫杨戬疾言厉色之态，唬得一怔尚未回话便又闻他道“奉劝仙子收收手脚，少去些华山，不然往日犯到杨某手上便如此珠！”他说话间衣袖一振拂过她鬓发，下刻手掌一展那莹莹花钿东珠便已作齑粉。")]),v._v(" "),_("p",[v._v("嫦娥又惊又恼一张俏脸涨得紫红，末了重重哼了声，踏云而去，她方走远便有男声低低响起“方才你若有半分好脸色，我便背后给你一锏。”")]),v._v(" "),_("p",[v._v("杨戬吐口浊气复道“她背后亦是盘根错节，你且思量思量自己罢，侍郎大人”敖摩昂轻弹鹤袍衣袖，嗤笑道“咱俩叫一根绳栓得紧，往后还请天神”摩昂有意拿腔捏调学那女仙唤他“多加提携！”")]),v._v(" "),_("p",[v._v("杨戬剑眉微蹙，答非所言“她好些了嘛？”摩昂脸色立时难看冷声道“真君心脏刚健，吊着舍妹气长些了。”杨戬闻言有些急切“我明日遣了仲景去瞧瞧罢。”")]),v._v(" "),_("p",[v._v("摩昂摆手一挥不耐道“人现下入了上天仙籍，目标恁大了些，你少来！”他说着也皱起眉宇，长叹一声“身骨具好了，却仍不见醒，你多顾着些自个罢，权当为了三妹心脏好。”")]),v._v(" "),_("p",[v._v("杨戬闻言不觉抚上心门，瞧着天河涛涛恍然如见西海，心下怅然：我爱的姑娘或许明日醒来，或许永不醒来。")])])}),[],!1,null,null,null);_.default=n.exports}}]);