(window.webpackJsonp=window.webpackJsonp||[]).push([[491],{861:function(r,n,t){"use strict";t.r(n);var v=t(5),_=Object(v.a)({},(function(){var r=this,n=r._self._c;return n("ContentSlotsDistributor",{attrs:{"slot-key":r.$parent.slotKey}},[n("h2",{attrs:{id:"前置声明"}},[n("a",{staticClass:"header-anchor",attrs:{href:"#前置声明"}},[r._v("#")]),r._v(" 前置声明")]),r._v(" "),n("p",[r._v("所有分析为笔者执笔当时的理解，笔者的理解随着时间推移也会有变化甚至修正，请列为看官注意每篇分析的写作时间，原你我一同与时俱进，特此共勉。")]),r._v(" "),n("h1",{attrs:{id:"一点简单的戬心身份属性分析"}},[n("a",{staticClass:"header-anchor",attrs:{href:"#一点简单的戬心身份属性分析"}},[r._v("#")]),r._v(" 一点简单的戬心身份属性分析")]),r._v(" "),n("p",[r._v("宝前世界中没有把政治隐喻拍得很明显，所以杨戬的身份一直在变化也只是偶尔点一句没有说透，按时间线分析一下。"),n("br"),r._v("\n瑶姬杨天佑结婚，杨蛟杨戬杨婵的身份都是凡间普通人，也就是“良民”，政治上清白。"),n("br"),r._v("\n瑶姬私自下凡婚配的事被揭穿，被压桃山，杨天佑杨蛟死亡，杨戬杨婵是天廷“通缉犯”。这也是玉鼎要他们藏起来的原因，他是阐教出身，不能明面上和玉帝这个三界之主翻脸，只能私下“窝藏通缉犯”。"),n("br"),r._v("\n杨戬救母失败、斧劈九大金乌、坠入西海被寸心所救，两人互有好感，这段时间内，杨戬的身份一直是“通缉犯”，这也是寸心要为杨戬治水请功的原因，也是戬心婚前婚后矛盾的起点——寸心是西海公主，她身后有一海的水族。如果她要和杨戬在一起，那么杨戬必须“政治上清白”，两人的婚姻才能名正言顺，西海也不会存在政治危机。"),n("br"),r._v("\n杨戬虽然天资过人，可是他从出生到救母期间，一直在学习的都是生存技能、神通本领，玉鼎没有教他政治方面的知识，他此时的政治智慧只能说大于零。寸心不同，她的政治敏锐性非常高，以治水为例，她在杨戬想要治水前，只是以龙族身份去救人，和天蓬等人行动并没有宣传出去，杨戬找到她希望画图，她立刻意识到治水可以一事二用，一来救护百姓解除灾害，二来为杨戬拿到玉帝赦免，重新取回“良民”身份，他们的婚事就没有阻碍了。可是在杨戬来说，治水救人是出于本心，拿到赦免向天廷低头、向仇人求饶，万万不行！这期间还有个小插曲，杨戬和寸心说起他要找天蓬元帅，过去天蓬在不损害自己的前提下多次帮助杨戬，他理所当然的把天蓬视为自己阵营的人，忽略了天蓬的政治立场。寸心对于他的想法，只是一笑，她立刻断定天蓬不会让杨戬如愿，因为他是天廷的“元帅”，她没有阻止杨戬去找人，她知道劝说无用，只有让杨戬亲自体会过，他才能认识到“私人身份的友善≠政治立场的同盟”，果然，杨戬经此一事，学乖了，他再和天蓬、嫦娥、包括后面出现的金蝉子打交道时，都会把私人身份和政治立场区别开，只是碍于剧情的篇幅，没有表现得很明显。"),n("br"),r._v("\n弱水重回天界，治水结束，王母南天门封众圣，杨戬身份“洗白”成功，他有了神位，但是他没有发现王母耍了个小花招，他受限于天规，不能成亲，寸心是龙族，可以成亲。如果他放弃寸心，等于他有了神位就对帮他治水立功的恋人背信弃义，也就是现在人们常说的“上岸第一剑，先斩意中人”，他会在三界众生的注视下塌房，重情重义人设崩塌。如果他成亲，就是他违背天规，明知故犯，有神位的前提下，天廷处罚名正言顺，西海就是共犯。寸心被西海扣押、杨戬抢亲，是他唯一能成亲又不在明面上连累西海的办法，寸心虽然知道这个举动会有潜在风险，但是她此刻对天廷抱有幻想，所以她也选了成亲，想着来日方长，慢慢想办法找机会立功，帮助杨戬的“明知故犯”可以再次洗白。"),n("br"),r._v("\n不客气的说，杨戬这时已经是为爱情冲冠一怒犯天条，他神位仍在是天廷内里虚弱，如果再次通缉他、或是撤销神位、和他硬碰硬，只会两败俱伤，所以只能由西海出手扣下寸心试图阻止婚事。但是杨戬强行抢亲，西海的阻拦失败，暂时在明面上摘出去了。杨戬虽然没有明着被通缉，可是他实质上还是玉帝的眼中钉。"),n("br"),r._v("\n寸心的身份更复杂，她是西海的公主、想要保护西海的水族，她是杨戬的妻子，她希望杨戬可以和天廷和解永远平安，在没有达成这两个目的之前，她是狭缝里生存的可怜人，有一句歇后语“风向里的老鼠——两头受气”，就是她的真实写照。"),n("br"),r._v("\n新婚之日，元始天尊传令，封神即将开始，命杨戬奉元始天尊之令前去参战，一是他公开宣布维护杨戬这个三代弟子，并在杨戬挑衅天廷的前提下，公开承认戬心婚姻，二是给杨戬一个拖延时间的机会，他在封神期间壮大实力。这期间，杨戬在武王、姜子牙等人的影响下，政治智慧突飞猛进。"),n("br"),r._v("\n封神结束，杨戬回灌江口过他的日子，天廷实力也得到增强，对杨戬这股明面上存在的逆反势力蠢蠢欲动了。杨戬意外发现灌江口埋伏天兵天将，才知道玉帝从来没有和他维护塑料和平的意思，他为了不让寸心担心，对她封锁消息，这也导致一个严重的后果，寸心在婚内对局面的判断出现极大的失误。她在杨戬除妖时想要上报天廷，是希望可以效仿治水时期的做法，再次和天廷达成和解，但是杨戬知道这是无用功，所以他坚决不肯，寸心的视角来看，她为了和杨戬成亲给西海带来了政治隐患，而杨戬却不肯为了她娘家的安全付出一点点行动。"),n("br"),r._v("\n西游时期，是有一点和封神不一样的地方，杨戬是被动卷入事件。在三界看来，唐僧取经的只看表面就是天廷灵山借此下棋，三界重新洗牌，但是这次的过程是封神那么残忍、基本上各路人马有来无回，还是走走过场，完全无法判断。杨戬如果参与进来的话，他拿到第二次赦免的机会并不大，天廷若是会给他的婚姻一个特殊的赦免，那么天规无异于摆设，告诉三界“只要立功就可以违反天规”。"),n("br"),r._v("\n如果杨戬不参与西游，也很困难，五哥狐妹做为试图伤害唐僧的妖精、小玉做为他们的孩子，不管杨戬杨婵谁收养她，都无异于直接给天廷把柄，只要追责就可以名正言顺的处理他们，小玉的存在，再次把戬心推向三界的风口浪尖。"),n("br"),r._v("\n杨戬是天廷的眼中钉、寸心是西海风险的导火索。"),n("br"),r._v("\n这次危机的化解只能感谢姥姥，她做为狐妹的母亲，带走外孙的神来一笔，意外解决戬心的困境。"),n("br"),r._v("\n凤仙郡时期，戬心和离。杨戬的身份再次转换，成了“为司法天神之位抛弃妻子的小人”“向玉帝低头亲口否认瑶姬的不孝子”，他成了“仇人的走狗”，在哪吒等人的眼中，他彻彻底底崩了人设塌了房。寸心回到西海，仍是西海三公主，成了外人眼中这桩婚事的受害者。这个时间点，杨戬的政治素养是有一些，但不多，他没有意识到如果他想要为三界做点好事，那么天规的存在本身就是一种压迫一种限制，而是采用了真假两套的糊弄做法，在真实压迫不可动摇的基础上试图用虚假取代真实，这不是在沙滩上建设城堡，这是白日做梦。"),n("br"),r._v("\n随着杨戬在天廷处理楠郡、李博安、和各种阳奉阴违，他的走狗人设越发深入人心，而他越招人恨，寸心在外人眼中就越可怜，他是为权势不择手段的司法天神，不再是昔日爱护百姓的二郎真君，她是痴心错付结婚千年最终惨淡收场的西海三公主，不再是婚姻期间可能连累龙族的导火索。"),n("br"),r._v("\n天奴揭发了杨戬处理的楠郡之事，寸心顶罪，事件结局是杨戬的司法天神之位保住了，寸心失去了公主身份永禁西海。这里剧组的拍摄手法很有趣，和观众玩了个信息差，真实情况只有在场的玉帝王母、戬心等人知晓，观众的上帝视角知晓，剧内，三界收到的消息是寸心闯入凌霄宝殿揭发杨戬，处理结果是杨戬这个司法天神全身而退，寸心这个西海公主失去自由和身份。杨戬不假思索御前出刀、将寸心的打出天界万劫不复改成了居家隔离，只有他们寥寥数人看到，对于三界来说，寸心因为杨戬失去身份有了政治污点，杨戬这是塌成废墟的房子还能再来一次地震。哪吒等人在宝莲灯时间线里那么恨他也不奇怪了，哪吒可是二郎神的粉转cp粉、随着cp的be又转黑了。"),n("br"),r._v("\n杨戬这时的政治素养已经是完全体，他意识到瑶姬的悲剧起点是天规、他和寸心的矛盾源头是天规、他想要为三界做事的阻力还是天规，他如果想改变这一切，唯一能做的就是改变，就是推出新天条，可他现在的身份是旧天条的维护者，是唯一有机会对上糊弄对下放纵、给新天条打掩护的人，是寸心失去她的身份她的自由给他换来的仅有一次的机会，那么他必须做一个藏在暗处的人，去推动明处的人来主导这场变革。至此，他完成了他最后一次身份的转变，开场时，他得到、他失去。戬心相逢时，他是犯人，她是公主，现在，他走上高峰，她坠入低谷，他得到、她失去，他的责任即将开始，她的任务已经完成。他们也完成了宝前、宝莲灯世界大男主的成长线。在这条线上，戬心的身份属性一直是互相对照，他们转变的整个过程就是双螺旋曲线，永远面对彼此、不可分割、不可失去。")])])}),[],!1,null,null,null);n.default=_.exports}}]);